        words = (rf'{words1}|{words2}|{words3}|{words4}|{words5}|{words6}|{words7}|{words8}|{words9}|{words10}|'
                 rf'{words11}|{words12}|{words13}|{words14}|{words15}|{words16}|{words17}|{words18}')
